ment.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/data/catalog/indexRound.do?model.tableName=\"+cltName;\r\n     }\r\n\r\n     function queryCatPhases(cltName){\r\n         //alert(cltName);\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/data/catalog/indexPhase.do?model.tableName=\"+cltName;\r\n     }\r\n     function queryCatSeeds(cltName){\r\n         //alert(cltName);\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/data/catalog/indexSeed.do?model.tableName=\"+cltName;\r\n     }\r\n     function setMailsOfSeis(){\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/email/seismic/index.do\"\r\n     }\r\n     function stationSeeds(){\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/station/stationSeeds.do\"\r\n     }\r\n\r\n     function queryStations(){\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/station/list.do\"\r\n     }\r\n     function queryInstrument(){\r\n         document.getElementById(\"main_info\").style.display=\"none\";\r\n         document.getElementById(\"cta_info\").style.display=\"\";\r\n         window.parent.main.location.href=\"${ctx}/quake/seismic/instrument/index.do\"\r\n 